export class IntervalClassCounter extends Component {
    constructor(props) {
      super(props)

      this.state = {
         count:0
      }
    }
    componentDidMount(){
        this.interval=setInterval(this.tick,1000)
    }
    componentWillUnmount(){
        clearInterval(this.interval)
    }
    tick=()=>{
        this.setState({
            count:this
        })
    }
  render() {
    return (
      <div>

      </div>
    )
  }
}

export default IntervalClassCounter
